mapper.insert:
INSERT INTO REVIEW_TB (
    REV_SQ, REV_SCO, CK_REV_ST, REV_CONT, REV_REG_DT, RES_CD
) VALUES (
    REV_SEQ.NEXTVAL,
    #{revSco},
    #{ckRevSt},
    #{revCont},
    DEFAULT,
    #{resCd}
)

mapper.select:
SELECT COUNT(*)
FROM RESERVATION_TB R
JOIN ROOM_TB RM ON R.ROOM_NO = RM.ROOM_SQ
WHERE R.RES_CD = #{resCd}
AND RM.ACCOM_NO = #{accomSq}
